INSERT INTO `products` (`id`, `category_id`, `title`, `avatar`, `price`, `amount`, `discount`, `summary`, `content`, `status`, `seo_title`, `seo_description`, `seo_keywords`, `created_at`, `updated_at`) VALUES
(1, 3, 'Nho đen Chile', '1656745965-product-pd-6.jpg', 300000, 50, 10, '', 'Tuy không nổi tiếng như nhiều loại nho nhập khẩu khác nhưng nho đen Chile vẫn khá được ưa chuộng tại nhiều gia đình vì tươi ngon và nhiều dưỡng chất. Nho đen Chile cũng là nho không hạt, vỏ màu đen đậm và bóng mượt, vị ngọt đậm thanh mát. Về giá trị dinh dưỡng, loại nho này không hề thua kém bất kỳ loại nho nhập khẩu nào khác. Hơn nữa, nho đen được đánh giá rất cao về việc chứa nhóm sắc tố thực vật tốt cho tim mạch.', 1, '', '', '', '2022-07-02 07:12:45', '2022-07-20 15:10:42'),
(2, 3, 'Táo envy', '1656746067-product-product-8.jpg', 125000, 121, 0, '', 'Táo Envy có nguồn gốc từ New Zealand nhưng hiện được trồng phổ biến tại Mỹ, Chile... Đây là một trong các loại táo ngon nhất thế giới với vị ngọt thơm đặc biệt, chưa kể đến vẻ ngoài cũng cực kỳ khiến các loại táo khác “ghen tị”. Táo Envy rất cứng, giòn và thơm, có khả năng tạo ngay ấn tượng mãnh liệt ngay từ lần thử đầu tiên. Vỏ bên ngoài đỏ thẩm tựa màu rượu vang tuyệt đẹp, thịt bên trong màu trắng tinh khôi, có thể giữ màu đến khoảng 10 tiếng đồng hồ - hơn hăn so với các loại táo khác.', 1, '', '', '', '2022-07-02 07:14:27', '2022-07-23 09:59:08'),
(3, 3, 'Dưa hấu sài gòn', '1656746158-product-product-7.jpg', 20000, 70, 0, '', '', 1, '', '', '', '2022-07-02 07:15:58', '2022-07-23 09:59:22'),
(4, 3, 'Xoài Úc', '1656746246-product-product-6.jpg', 130000, 67, 0, '', '', 1, '', '', NULL, '2022-07-02 07:17:26', NULL),
(5, 2, 'Rau cải ngọt', '1656746309-product-product-details-3.jpg', 10000, 67, 0, '', 'Có thể nói, rau cải là một trong những loại thực phẩm đã quá quen thuộc với mâm cơm Việt. “Họ hàng” nhà rau cải vô cùng đa dạng, cải thìa, cải xoong, cải bẹ, cải rổ…. và đương nhiên không thể thiếu “người anh em” cải ngọt. Họ nhà cải sở hữu rất nhiều lợi ích và cải ngọt cũng không ngoại lệ. Bài viết này, hãy cùng chúng tôi tìm hiểu về lợi ích của rau cải ngọt đối với sức khỏe cũng như các món ngon từ loại rau này nhé.', 1, '', '', NULL, '2022-07-02 07:18:29', NULL),
(6, 1, 'Thịt bò Mỹ', '1656746387-product-product-1.jpg', 500000, 12, 0, '', 'Là sự xen kẽ hoàn hảo giữa lớp thịt và lớp mỡ trên miếng thịt bò tạo nên những hình vân cẩm thạch đẹp mắt. Thịt bò mềm, mọng nước, hợp để làm steak hoặc nhúng lẩu', 1, '', '', '', '2022-07-02 07:19:47', '2022-07-15 15:04:15'),
(7, 1, 'Cá hồi', '1656746531-product-2900456.jpg', 450000, 45, 10, '', 'Cá hồi vốn là loại thực phẩm quý hiếm, không chỉ phục vụ nhu cầu ăn uống như làm cá chiên, vi cá nướng muối ớt, đút lò mà còn là thành phần hỗ trợ làm đẹp, tăng cường sức khỏe.', 1, '', '', '', '2022-07-02 07:22:11', '2022-07-20 15:09:25'),
(8, 2, 'Mướp hương', '1656752023-product-tải-xuống.jpg', 5000, 32, 0, '', 'Mướp là một loại cây thảo dạng dây leo, được trồng để lấy quả xanh. Với nhiều chất dinh dưỡng và công dụng đặc biệt tốt cho sức khỏe, mướp được nhiều người lựa chọn trong bữa ăn gia đình. Mướp hương có vị thơm ngát, ăn ngọt mát', 1, '', '', '', '2022-07-02 07:26:08', '2022-07-02 15:53:43'),
(10, 1, 'Lườn gà', '1658282239-product-1658065198-product-lườn-gà_cleanup.jpg', 60000, 50, 15, '', 'Thịt gà có tác dụng bồi bổ cho người bị bệnh lâu ngày, suy yếu không hấp thụ được thức ăn. Theo các nhà dinh dưỡng học, ngoài những chất albumin, chất béo, thịt gà còn có các vitamin A, B1, B2, C, E, axit, canxi, photpho, sắt. Đây là loại thực phẩm chất lượng cao, cơ thể con người dễ hấp thu và tiêu hóa. ', 1, '', '', '', '2022-07-17 06:39:58', '2022-07-20 15:09:49'),
(11, 1, 'Thịt ba chỉ', '1658282195-product-1658065333-product-Ba-rọi-heo.jpg', 100000, 50, 0, '', 'Thịt ba chỉ còn gọi là thịt ba rọi. Phần thịt này không có xương, nhiều mỡ, cắt ra từ bụng của con heo. Phần thịt này được gọi là ba chỉ vì khi cắt ngang miếng thịt mỡ sẽ thấy được thịt và mỡ xen kẽ 3 lớp với nhau theo thứ tự lớp thịt, lớp mỡ, rồi lớp thịt. Thịt ba chỉ được dùng phổ biến trong ẩm thực Đông Á, Châu Âu và Bắc Mỹ. Thịt heo rất giàu khoáng chất như phốt pho, selenium, natri, kẽm, kali và đồng. Ngoài ra, nó còn chứa hàm lượng sắt và magiê cao, tuy nhiên lượng canxi và mangan thì khá ít ỏi. Thịt heo là một nguồn cung vitamin phong phú như vitamin B1, B2, B5, B6, B12 ,PP. Vitamin A và E.', 1, '', '', '', '2022-07-17 06:42:13', '2022-07-20 08:56:35'),
(12, 1, 'Bồ câu nguyên con', '1658282165-product-1658065408-product-bồ-câu.jpg', 100000, 30, 0, '', 'Thịt chim bồ câu đã được làm sạch nên có thể chế biến ngay. Bề mặt da láng vàng, thịt săn chắc và có màu đỏ tươi. Khi nấu món ăn có mùi thơm, thịt mềm mà không hề có mùi hôi. Thịt chim bồ câu chứa hàm lượng protein cao, nhiều vitamin như A, B1, B2, E và các nguyên tố vi lượng tái tạo hồng cầu giúp điều trị thiếu máu hiệu quả. Do vậy giá trị dinh dưỡng trong thịt chim bồ câu cao hơn thịt gà, thịt bò.', 1, '', '', '', '2022-07-17 06:43:28', '2022-07-20 08:56:05'),
(13, 1, 'Cốt lết heo', '1658282141-product-1658065471-product-cốt-lết-heo.jpg', 100000, 30, 0, '', 'Sườn cốt lết (pork chop) là phần thịt được chặt vuông góc với xương sống của heo, thường có xương sườn hoặc một phần của đốt sống heo. Tên gọi “cốt-lết” có nguồn gốc từ tiếng Pháp, cotlete de porc, là phần thịt ở lưng heo, rất nạc và mềm. Ở miền Bắc thường lọc riêng khúc thịt này và hay được sử dụng để làm ruốc hoặc rim mặn. Còn ở miền nam, thịt cốt - lết rất phổ biến với món cơm tấm sườn thơm ngon. Thịt heo rất giàu khoáng chất như phốt pho, selenium, natri, kẽm, kali và đồng. Ngoài ra, nó còn chứa hàm lượng sắt và magiê cao, tuy nhiên lượng canxi và mangan thì khá ít ỏi.', 1, '', '', '', '2022-07-17 06:44:31', '2022-07-20 08:55:41'),
(14, 1, 'Gà ác nguyên con', '1658282101-product-1658065575-product-gà-ác.jpg', 45000, 35, 0, '', 'Gà ác loại nhỏ có kích thước bằng nắm tay là một giống gà quý, toàn thân và chân có màu đen ,có 5 ngón, thịt màu đen và rất bổ dưỡng. Đặc trưng của giống gà ác là bộ lông trắng không mượt nhưng toàn bộ da, mắt, thịt, chân và xương đều đen, chân có 5 ngón. Gà ác là giống gà có giá trị cao về mặt dinh dưỡng. Theo Đông y, gà ác vị ngọt, mặn, tính bình hơi ấm, hương vị thơm, tính ẩm, không độc, có công hiệu từ bổ gan thận, ích khí bổ huyết, tư âm thanh nhiệt, điều kinh hoạt huyết, chữa rong huyết…', 1, '', '', '', '2022-07-17 06:46:15', '2022-07-20 08:55:01'),
(15, 1, 'Gà nguyên con', '1658282068-product-1658065731-product-gà-nguyên-con.jpg', 110000, 40, 0, '', 'Thịt gà có nhiều phần nạc và tương đối ít mỡ, nên chứa một hàm lượng protein cao và đa dạng. Chính vì vậy, bổ sung các món ăn từ gà vào thực đơn hàng ngày sẽ mang lại cho người tiêu dùng nhiều lợi ích về sức khỏe, từ phát triển cơ bắp cho đến giảm cân hiệu quả. Ăn thịt gà có thể giúp mang tới một hàm răng và xương chắc khỏe. Bởi trong thịt gà có rất nhiều Phosphor – chất có lợi cho răng và xương. Ngoài ra, chất này còn góp phần đảm bảo các chức năng của các bộ phận như thận, gan, thần kinh trung ương,… giúp chúng hoạt động tốt hơn.', 1, '', '', '', '2022-07-17 06:48:51', '2022-07-20 08:54:28'),
(16, 1, 'Thịt heo xay', '1658065844-product-F158625_f39d.jpg', 85000, 30, 0, '', 'Thịt Lợn Xay được sơ chế sạch, xay thành các sợi nhỏ và đóng gói kín mang lại sự tiện lợi cao cho người sử dụng. Sản phẩm được lấy từ nguồn nguyên liệu tự nhiên, không dịch bệnh, đảm bảo vệ sinh an toàn thực phẩm. Thịt heo rất giàu khoáng chất như phốt pho, selenium, natri, kẽm, kali và đồng. Ngoài ra, nó còn chứa hàm lượng sắt và magiê cao, tuy nhiên lượng canxi và mangan thì khá ít ỏi. Thịt heo là một nguồn cung vitamin phong phú như vitamin B1, B2, B5, B6, B12 ,PP. Vitamin A và E.', 1, '', '', '', '2022-07-17 06:50:44', '2022-07-17 20:51:01'),
(17, 1, 'Bít tết bò Úc', '1658282031-product-1658066053-product-bit-tết-bò-úc_cleanup.jpg', 250000, 20, 10, '', 'Đàn bò được nhập khẩu hoàn toàn từ Úc, một nước đạt tiêu chuẩn an tòan cao về mặt dịch tễ, không có dịch bệnh trên gia súc. Đặc biệt,  thịt lấy từ bò cái tơ chưa qua vỗ béo để tiến hành giết mổ với các ưu điểm sau: thịt không mỡ phù hợp với tiêu dùng của người Việt Nam nhưng vẫn đảm bảo thịt có được độ mềm mại vừa phải.', 1, '', '', '', '2022-07-17 06:54:13', '2022-07-24 16:15:30'),
(18, 1, 'Bò Úc xay ', '1658281994-product-1658066238-product-bò-xay_cleanup.jpg', 210000, 20, 15, '', 'Thịt bò được sơ chế sạch, xay thành các sợi nhỏ và đóng gói kín mang lại sự tiện lợi cao cho người sử dụng. Sản phẩm được lấy từ nguồn nguyên liệu tự nhiên, không dịch bệnh, đảm bảo vệ sinh an toàn thực phẩm. Thịt bò rất giàu vitamin và khoáng chất, đặc biệt là chất kẽm và sắt. Do đó, cần bổ sung thịt bò cho mỗi khẩu phần ăn phù hợp để có được chế độ ăn dồi dào năng lượng mang lại sức khỏe tốt cho cơ thể. ', 1, '', '', '', '2022-07-17 06:57:18', '2022-07-20 15:11:01'),
(19, 1, 'Thit thăn phi lê heo', '1658066365-product-F158631_icon_e67c_cleanup.jpg', 75000, 10, 0, '', 'Thịt thăn phi lê heo là miếng thịt cắt từ bắp thịt, nằm phía trước và chạy dọc cột xương sống của con heo gần dưới phía chân sau. Miếng thịt nạc này mềm vì động vật bốn chân không dùng nó khi chuyển động mà tác dụng chính là để giữ dạng thẳng của cơ thể. Thịt heo rất giàu khoáng chất như phốt pho, selenium, natri, kẽm, kali và đồng. Ngoài ra, nó còn chứa hàm lượng sắt và magiê cao, tuy nhiên lượng canxi và mangan thì khá ít ỏi. Thịt heo là một nguồn cung vitamin phong phú như vitamin B1, B2, B5, B6, B12 ,PP. Vitamin A và E...Thịt heo là một loại thịt đỏ có hàm lượng protein cao và chứa nhiều vitamin thiết yếu, khoáng chất và các axít amin tốt cho sức khỏe.', 1, '', '', NULL, '2022-07-17 06:59:25', NULL),
(20, 1, 'Ba chỉ bò Mỹ thái lát 300gr', '1658066957-product-ba-chi-bo-thai-lat-dai-st-helens-my-500g-01800317309_1000_750_5f47912b-d379-4ac6-8ea4-47d4f9ebbd62.jpg', 80000, 60, 0, '', 'Ba chì bò được cắt mỏng bằng máy cắt chuyên dụng, an toàn và vẫn giữ được độ ngọt thịt, sẵn sàng cho nhúng lẩu, nướng, nấu phở hay cuốn nấm siêu ngon. Phần thịt ba chỉ bò nằm ở phần bụng, chen mỡ không hề ngấy, mềm ngọt và thơm thịt bò.', 1, '', '', NULL, '2022-07-17 07:09:17', NULL),
(21, 1, 'Thịt chân giò heo', '1658070655-product-thit-chan-gio-rut-xuong-lon-01020015300_1000_750_fe63f7bd-eda3-433c-8de9-7d3efc186d3b_cleanup.jpg', 65000, 20, 0, '', 'Thịt chân giò lợn rút xương là phần thịt ngon của con lợn, giàu dinh dưỡng và chứa nhiều khoáng chất như: Kẽm, Photpho, Sắt và rất nhiều vitamin... giúp tăng cường hệ miễn dịch, cung cấp năng lượng. Các vitamin A và B giúp ngăn ngừa các bệnh về da, hỗ trợ sức khỏe cho mắt. Đặc biệt trong Thịt chân giò lợn rút xương có chứa Vitamin B12 bổ máu và hỗ trợ não bộ hoạt động tốt hơn.', 1, '', '', NULL, '2022-07-17 08:10:55', NULL),
(22, 1, 'Cánh gà khúc giữa', '1658070925-product-canh-ga-khuc-giua-cap-dong-1kg-17830317302_1000_750_595e9788-9763-4054-8a25-aefadc76e0c1_cleanup.jpg', 50000, 40, 0, '', 'Cánh gà từ trước đến nay vẫn luôn là món ăn ưa thích của nhiều người, đặc biệt là các bạn nhỏ. Phần thịt này có hương vị thơm ngon, mềm, ẩm và có thể biến tấu thành nhiều món ăn đa dạng, hấp dẫn. Khúc giữa có thể coi là phần ngon nhất của cánh gà bởi sự kết hợp hài hòa, vừa đủ giữa thịt nạc và da béo ngậy. Thịt ở phần này không quá nhiều, mềm mà chắc, ngọt, không bở. Bao quanh thịt là lớp da mỏng giữ cho thịt mọng nước, có độ béo ngậy nhưng không gây ngấy cho người ăn. Chính vì vậy, nhiều nhà hàng và gia đình chỉ mua riêng phần khúc giữa để chế biến. Cánh gà khúc giữa là phần nhiều nạc lại ít ngán nhất, ngon nhất của cánh gà.', 1, '', '', NULL, '2022-07-17 08:15:25', NULL),
(23, 1, 'Sườn non heo', '1658071334-product-F158626_icon_a16a_cleanup.jpg', 180000, 35, 0, '', 'Sườn non là một trong những phần thịt ngon nhất, được lóc ra từ xương sườn của con heo. Sườn non có phần xương sườn nhỏ, xương nhỏ, dẹt, nhiều thịt, ít xương và thường có sụn. - Thịt sườn non rất ngon, ăn giòn và có vị ngọt. Sườn non có thể chế biến được nhiều món như nướng, chiên, xào chua ngọt hoặc nấu canh. Thịt heo rất giàu khoáng chất như phốt pho, selenium, natri, kẽm, kali và đồng. Ngoài ra, nó còn chứa hàm lượng sắt và magiê cao, tuy nhiên lượng canxi và mangan thì khá ít ỏi. Thịt heo là một nguồn cung vitamin phong phú như vitamin B1, B2, B5, B6, B12 ,PP. Vitamin A và E.', 1, '', '', NULL, '2022-07-17 08:22:14', NULL),
(24, 1, 'Thịt thăn bò Mỹ', '1658071915-product-Than-ngoai-bo-my-2.jpg', 250000, 10, 0, '', 'Thăn ngoại bò Mỹ là phần thịt nằm gần mông và dẻ sườn cuối. Đặc trưng của phần thịt này là mềm, tỉ lệ mỡ - nạc đan xen hợp lý mang đến hương vị thơm ngon. Thăn ngoại bò Úc giàu chất sắt có tác dụng phòng tránh cho cơ thể bị thiếu máu, thành phần axit amin, protein thích hợp với những người có thể chất yếu. Do đó, sử dụng thăn ngoại bò Úc giúp cải thiện sức đề kháng của cơ thể. ', 1, '', '', NULL, '2022-07-17 08:31:55', NULL),
(25, 1, 'Thịt vịt nguyên con', '1658072252-product-Vit-tuoi-nguyen-con_cleanup.jpg', 85000, 40, 0, '', 'Thịt vịt là một trong những thực phẩm có giá trị dinh dưỡng khá cao. Trong 100g thịt vịt có khoảng 25g chất protein (vượt xa nhiều lần so với thịt bò, heo, dê, cá, trứng). Ngoài ra, còn có cả hàm lượng các chất dinh dưỡng như canxi, phốt pho, sắt, vitamin (B1, B2, A, D, E), acide nicotic…Thịt vịt có nguồn gốc rõ ràng, đảm bảo vệ sinh an toàn thực phẩm. Vịt tươi nguyên con được lựa chọn từ những nông trại chăn nuôi lớn, được kiểm dịch thường xuyên và chăm sóc theo quy trình hiện đại, an toàn.', 1, '', '', NULL, '2022-07-17 08:37:32', NULL),
(26, 2, 'Bắp cải tím', '1658072761-product-bap-cai-tim.jpg', 30000, 50, 0, '', 'Bắp cải tím tròn, các lá cứng xếp chồng và cuộn chặt vào nhau. Bên trong lõi có màu trắng, các lá màu tím đậm rất đẹp mắt. Vị và mùi của bắp cải tím dịu hơn bắp cải khác, mùi ít hăng hơn. Bắp cải tím có hoạt chất anthocyanin - một chất chống oxy hóa mạnh, cùng với các hợp chất thực vật và các vitamin và khoáng nên hỗ trợ nhiều chức năng của cơ thể: giảm cân, giúp chống viêm, ngăn ngừa ung thư, tăng khả năng miễn dịch,...', 1, '', '', NULL, '2022-07-17 08:46:01', NULL),
(27, 2, 'Bông cải trắng', '1658073298-product-bong-cai-trang-loai-1kg_cleanup.jpg', 20000, 50, 0, '', 'Bông cải hay còn gọi là súp lơ. Trong súp lơ rất giàu chất xơ và đây là thành phần rất cần thiết cho hệ tiêu hóa của bạn. Chất xơ giúp hệ tiêu hóa hoạt động ổn định, ngăn ngừa táo bón, giúp cơ thể thải độc hiệu quả qua đường bài tiết. Nhờ vậy mà hệ tiêu hóa hoạt động trơn tru, giúp bạn có cơ thể khỏe mạnh. Loại rau này  còn có tác dụng giúp xương chắc khỏe và ngăn ngừa bệnh loãng xương hiệu quả. Vì trong súp lơ chứa rất nhiều vitamin K và canxi - đây là hai chất rất cần thiết giúp xương chắc khỏe hơn.', 1, '', '', '', '2022-07-17 08:54:58', '2022-07-17 22:58:01'),
(28, 2, 'Bông cải xanh', '1658073658-product-bong_cai_xanh_cleanup.jpg', 15000, 45, 0, '', 'Bông cải xanh còn được gọi là Súp lơ. Bông cải xanh có chứa nhiều Vitamin A, Vitamin C, Vitamin K, Vitamin B9, Chất xơ, Kali, Phốt pho, các chất chống oxy hóa và một lượng nhỏ Selen, mang đến nhiều lợi ích cho sức khỏe…Bông cải xanh có chứa hàm lượng glucoraphanin cao, cùng đó là lutein và zeaxanthin, chính là những chất chống oxy hóa, giúp chống lại những tổn thương tế bào do gốc tự do gây ra, giảm viêm hiệu quả và có khả năng chống lại một số bệnh lý.', 1, '', '', NULL, '2022-07-17 09:00:58', NULL),
(29, 2, 'Cà chua', '1658281960-product-1658073880-product-ca-chua-2.jpg', 10000, 50, 0, '', 'Trong cà chua có chứa rất nhiều nước (khoảng 90%). Bên cạnh đó có nhiều chất dinh dưỡng tốt cho cơ thể như: Carotene, Lycopene, vitamin B6, vitamin C, vitamin A, Kali, Folate, Thiamin, Magie, Niacin, Đồng và Phốt pho, chất xơ và Beta Carotene. Cà chua cung cấp đủ các loại vitamin cần thiết cho mắt như vitamin A, C, K. Nhờ những vitamin này, thị lực mắt được tăng cường, ngăn ngừa chứng thoái hóa điểm vàng và chứng quáng gà ở một số người.', 1, '', '', '', '2022-07-17 09:04:40', '2022-07-20 08:52:40'),
(30, 2, 'Cà chua bi', '1658074253-product-Tomates_cerises_Luc_Viatour.jpg', 8500, 70, 0, '', 'Cà chua bi hay còn gọi là cà chua cherry, có xuất xứ từ Đà Lạt. Cà chua bi có kích thước nhỏ, tròn, kích thước các quả đều nhau, rất đẹp mắt. Cà chua bi có thịt giòn và mọng nước, khi cắn phảng phất mùi thơm đặc trưng. Hương vị rất đậm đà, vị ngọt thanh xen lẫn chút vị chua. Trong 100g cà chua bi có chứa nhiều chất dinh dưỡng như: vitamin A, vitamin B9,  Vitamin C,  Niacin, Calcium,... Cà chua bi giúp tăng thị lực, hỗ trợ ngăn ngừa bệnh quáng gà. Hỗ trợ hạn chế các bệnh về tim mạch, làm chậm quá trình hình thành các tế bào ung thư trong cơ thể…', 1, '', '', NULL, '2022-07-17 09:10:53', NULL),
(31, 2, 'Cà rốt', '1658074460-product-ca-rot-mini.jpg', 15000, 50, 0, '', 'Cà rốt vừa là thực phẩm lại vừa là dược liệu nhờ vào thành phần hóa học có trong đó. Đây là lý do chính mà cà rốt lại được sử dụng rộng rãi trên toàn thế giới. Trong cà rốt chứa rất nhiều chất mang lại những lợi ích cao cho sức khỏe con người. Thành phần lớn nhất có trong cà rốt là nước, trong một củ chiếm khoảng 88-95%. Ngoài thành phần quan trọng đó thì cà rốt còn cung cấp các chất: protein, carbohydrate, đường, chất xơ, chất béo, bão hòa, omega3 và omega6,... Bên cạnh đó, cà rốt còn chứa nguồn vitamin và khoáng chất dồi dào như: vitamin A, vitamin C, vitamin K, vitamin D, vitamin K, vitamin B1, B2, B6, B12,... Các khoáng chất cần thiết có trong cà rốt: canxi, sắt, magie, photpho, kali, natri, kẽm, đồng, mangan,...', 1, '', '', NULL, '2022-07-17 09:14:20', NULL),
(32, 2, 'Rau cần tây', '1658074629-product-27_f102291_rau_muong_3962a35b44254dcda56c59ebc01f7594_large_1594450316.jpg', 10000, 70, 0, '', 'Rau Cần tây là loại thực phẩm chứa đến 95% thành phần là nước. Chính vì thế nhiều người lầm tưởng rằng loại rau này không chứa nhiều chất dinh dưỡng. Thực tế đây là loại rau xanh chứa nhiều vitamin, khoáng chất thiết yếu cho sức khỏe cơ thể. Được biết đến là loại thực phẩm giàu vitamin K, có thể cung cấp lên đến 25% vitamin K mỗi ngày cho bạn. Ngoài ra, loại rau này còn chứa lượng nhỏ các vitamin khác như: vitamin A, vitamin B và vitamin C. Bên cạnh đó, hàm lượng calo trong rau rất thấp nhưng lại chứa một số khoáng chất nhất định như: kali, folate, choline, canxi, mangan, magiê, phốt pho,… ', 1, '', '', NULL, '2022-07-17 09:17:09', NULL),
(33, 2, 'Củ cải đỏ', '1658074959-product-Capture_cleanup.JPG', 25000, 40, 0, '', 'Trong củ cải đỏ có chứa nhiều chất dinh dưỡng như: kcal, chất béo, Natri, Kali, chất xơ, đường, vitamin A, vitamin C, canxi, sắt, vitamin B6,... Ngoài dùng làm nguyên liệu chế biến món ăn, củ cải đỏ còn được sử dụng làm thuốc chữa bệnh. Củ cải đỏ rất giàu vitamin A, C, E, K, vitamin nhóm B, cùng nhiều loại chất khoáng khác như: sắt, magie, betaine và axit folic,… Đây đều là những dưỡng chất thiết yếu của cơ thể.', 1, '', '', NULL, '2022-07-17 09:22:39', NULL),
(34, 2, 'Đậu cove', '1658075126-product-french-768x768_800x800.jpg', 15000, 30, 0, '', 'Đậu cove với tên gọi tiếng Việt là đậu que, và người dân cũng quen thuộc với cách gọi này hơn. Ngày nay, chúng được trồng tại nhiều nơi trên thế giới và được bán cả ở dạng khô và dạng tươi. Đậu cove cũng mang đến nhiều lợi ích cho sức khỏe như: Tăng cường sức khỏe tim mạch, hỗ trợ tiêu hóa, ngăn ngừa ung thư, giúp xương chắc khỏe, kiểm soát đường huyết, hỗ trợ giảm cân.', 1, '', '', NULL, '2022-07-17 09:25:26', NULL),
(35, 2, 'Đậu Hà Lan', '1658075349-product-dau-ha-lan.jpg', 15000, 40, 0, '', 'Đậu Hà Lan có 1 mảnh vỏ, màu xanh lục, bên trong có các hạt đậu hình tròn màu xanh giống như mảnh vỏ. Chiều dài và số lượng hạt trong đậu Hà Lan tùy thuộc vào giống và điều kiện phát triển. Thường thì mỗi tép có khoảng 5-10 hạt đậu.. Đậu Hà Lan với lượng chất chống oxy hóa cao, các hợp chất như saponin, vitamin , chất xơ,... giúp hỗ trợ sức khỏe cho cơ thể. Đậu Hà Lan giúp ngăn ngừa ung thư, giảm lượng đường trong máu, tốt cho sức khỏe tim mạch, tốt cho mắt, tăng cường hệ miễn dịch,....', 1, '', '', NULL, '2022-07-17 09:29:09', NULL),
(36, 2, 'Khoai lang Nhật', '1658075452-product-khoai-lang-nhat-ban.jpg', 40000, 30, 0, '', 'Khoai lang Nhật có củ thuôn dài, không quá to, kích thước vừa phải, có màu vỏ tím đậm vfa bóng mịn hơn các khoai khác. Khi ăn thì khoai cho hương thơm, vị ngọt, bở và màu ruột vàng đậm. Khoai lang Nhật mang đến nguồn dinh dưỡng lớn cho cơ thể, cung cấp lượng lớn tinh bột, chất xơ, các loại vitamin: A, C, B6, E,... các khoáng chất: kali, mangan,... giúp cơ thể giảm cân hiệu quả, làm mềm da, da giảm mụn, căng sáng, giảm cholesterol, giảm huyết áp, ngăn ngừa đột quỵ, hỗ trợ sức khỏe tim mạch,...', 1, '', '', NULL, '2022-07-17 09:30:52', NULL),
(37, 2, 'Khoai tây', '1658075641-product-dinh-duong-tu-khoai-tay.jpg', 25000, 35, 0, '', 'Khoai tây có hình dạng củ ô van, lớp vỏ và ruột đều có màu vàng. Khoai tây vàng  chứa nhiều chất dinh dưỡng và có vị ngon đặc trưng khi chế biến. Khi ăn khoai tây có vị ngọt, béo, nhiều bột, mềm. Khoai tây giàu vitamin và khoáng chất, đặc biệt là vitamin C và kali. Lượng vitamin C dồi dào giúp tăng cường hệ miễn dịch, thúc đẩy quá trình tái tạo tế bào và tăng khả năng hấp thụ chất sắt. Trong khi đó, Kali tốt cho huyết áp và giảm nguy cơ đột quỵ. Ngoài ra, khoai tây còn hỗ trợ đẩy nhanh quá trình tiêu hóa, làm đẹp da, tốt cho tim mạch,...', 1, '', '', NULL, '2022-07-17 09:34:01', NULL),
(38, 2, 'Mồng tơi', '1658075758-product-zkbqpdEfRjGlIso8mi1e_mongtoi_1-large@2x.jpg.jpg', 8500, 60, 0, '', 'Rau mồng tơi có lá màu xanh, rất tươi, dày, vị ngọt nhẹ. Khi ăn có độ nhớt, mềm rất dễ ăn. Rau mồng tơi giàu chất xơ, chất nhầy, đặc biệt giàu vitamin A, canxi, và các chất chống oxy hóa như beta carotene, lutein và zeaxanthin… giúp cơ thể giảm cân, hỗ trợ tiêu hóa, nhuận trường tốt, làm da mịn màng, cải thiện thị lực, tốt cho xương khớp,...', 1, '', '', NULL, '2022-07-17 09:35:58', NULL),
(39, 2, 'Nấm đùi gà', '1658075835-product-nam-dui-ga-tuoi-5.jpg', 150000, 20, 0, '', 'Nấm đùi gà với hình dáng tương tự như đùi gà, thân nấm màu trắng, mập và to, mũ nấm nhỏ, phẳng, màu nâu. Nấm đùi gà dai, vị ngọt và bùi, hương rất thơm khi nướng. Trong nấm đùi gà có chứa hàm lượng protein và các nguyên tố vi lượng canxi, sắt, kali, photpho,…nhiều vitamin nhóm B (như B1, B6, B12,…), C, D,… nhiều chất xơ và các axit amin cần thiết mà cơ thể không thể tự tổng hợp được. Vì thế nấm đùi gà hỗ trợ cho cơ thể rất nhiều: tăng khả năng miễn dịch, hỗ trợ cho người mắc bệnh tiểu đường, hỗ trợ tiếu hóa, kích thích vị giác, giảm mỡ máu, phòng chống ung thư,...', 1, '', '', '', '2022-07-17 09:37:15', '2022-07-17 23:44:47'),
(40, 2, 'Nấm hương tươi', '1658075991-product-nam-huong-nam-dong-co-1.jpg', 200000, 10, 0, '', 'Nấm đông cô tươi còn được gọi là nấm hương. Nấm đông cô có nguồn gốc từ Đông Á. Ngày nay, nấm được sử dụng phổ biến ở các nước Châu Á, đặc biệt là Nhật Bản và Trung Quốc. Ở Việt Nam, nấm hương được xem là loại nấm quý, được trồng ở hầu hết những phía bắc như Lào Cai, Lai Châu, Lạng Sơn, Cao Bằng, Bắc Kạn, Thái Nguyên, Hoà Bình và tại các trang trại hữu cơ ở Đà Lạt. Nấm hương cũng chứa nhiều vitamin B5, B3, B2, B6,...1 số khoáng: kẽm, mangan, selen, đồng,... cùng chất xơ, protein và các chất dinh dưỡng khác.\r\n\r\n', 1, '', '', '', '2022-07-17 09:39:51', '2022-07-17 23:45:06'),
(41, 2, 'Nấm linh chi nâu', '1658076530-product-nam-linh-chi-nau-bach-hoa-xanh-150g-202103242256093127.jpg', 180000, 15, 0, '', 'Nấm linh chi nâu có thân màu trắng, dài khoảng 3-5 cm, phần mũ màu nâu gắn liền với thân. Loại nấm này mọc thành cụm to không mọc đơn lẻ. Vị của nấm ngọt thanh tự nhiên kết hợp với độ giòn, độ dai làm hấp dẫn thêm cho món ăn. Một số người còn nhận định loại nấm này có hương vị như ăn hải sản. Nấm linh chi nâu ít calo, chất béo, giàu protit, chất xơ, vitamin nhóm B cùng các acid amin có lợi đem lại nhiều lợi ích cho cơ thể. Giúp tăng cường sức đề kháng, phát triển não bộ, cải thiện trí nhớ, giảm stress, tăng khả năng bài tiết, đào thải độc tố, điều hòa hoạt động của hô hấp và tim mạch,...', 1, '', '', NULL, '2022-07-17 09:48:50', NULL),
(42, 2, 'Nấm linh chi trắng', '1658076711-product-am-linh-chi-trang-tuoi-150g-nam-xanh-1.jpg', 200000, 10, 0, '', 'Nấm linh chi trắng còn được gọi với 1 số cái tên hay khác như: nấm bát tiên, nấm thủy tiên trắng. Nấm linh chi với nhiều chất dinh dưỡng và các vi lượng giúp cơ thể được nạp năng lượng, phòng chống ung thư, tốt cho trí não, tăng sức đề kháng, giúp giảm cân và điều hòa lượng máu hiệu quả,...', 1, '', '', NULL, '2022-07-17 09:51:51', NULL),
(43, 2, 'rau ngò', '1658076844-product-rau-ngo-1.jpg', 5000, 50, 0, '', 'Ngò có thân nhẵn, lá ở phần ngọn, lá chia làm 3 thùy có khía răng to và tròn. Ngò có mùi hăng, dùng để làm rau nêm cho các món ăn hay dùng để trang trí. Ngò rí chứa các chất dinh dưỡng cineole và axit linoleic, cineole, borneol, limonene, alpha pinene và beta phelandrene, vitamin A, C, B1, B2 và chất sắt,... giúp ích rất lớn đối với sức khỏe của cơ thể. Giúp điều trị sưng viêm, trị tiêu chảy, trị loét miệng, trị bệnh thiếu máu, rối loạn kinh nguyệt, làm đẹp da,...', 1, '', '', NULL, '2022-07-17 09:54:04', NULL),
(44, 2, 'Ớt chuông xanh', '1658076931-product-ot-chuong-xanh-da-lat-570x421-700x700.jpg', 15000, 40, 0, '', 'Ớt chuông xanh là loại thực phẩm đặc biệt, mặc dù cũng gọi là ớt nhưng loại quả này không hề có vị cay, ngược lại rất giòn, ngọt. Trong loại ớt này chứa rất nhiều dinh dưỡng như vitamin A, C, K,... cùng nhiều khoáng chất khác. Vì thế, khi bổ sung thực phẩm này vào thực đơn của gia đình, bạn sẽ giúp các thành viên trong nhà hấp thu thêm các chất dinh dưỡng cần thiết.', 1, '', '', NULL, '2022-07-17 09:55:31', NULL),
(45, 2, 'Rau lang', '1658077054-product-rau-lang-5.jpg', 10000, 35, 0, '', 'Rau lang gồm có phần lá và phần ngọn non, tất cả đều màu xanh lục, lá dạng như trái tim, nhọn ở đầu, trên lá có các gân lá xanh. Khi ăn rau lang giòn, vị ngọt và tính mát. Rau lang giàu các chất dinh dưỡng nước, protein, tinh bột, vitamin C, vitamin PP, nhiều chất khoáng như: canxi, sắt, phốt pho,…. mang đến nhiều lợi ích cho sức khỏe: giúp thanh nhiệt giải độc, nguồn chất chống oxy hóa, hạn chế quá trình lão hóa, ngăn ngừa táo bón, giảm cân, tốt cho tim mạch, tiểu đường,...', 1, '', '', NULL, '2022-07-17 09:57:34', NULL),
(46, 2, 'Rau muống', '1658077154-product-Rau-muống.jpg', 10000, 40, 0, '', 'Rau muống là loại rau quen thuộc trong ẩm thực, vì thế được nghiên cứu tìm các công dụng đối với sức khỏe cơ thể. Ăn rau muống giúp nạp nguồn vitamin dồi dào cho cơ thể, giúp trẻ hóa làn da, điều trị những bệnh ngoài da, ngăn ngừa lão hóa và giữ mái tóc chắc khỏe; rau muống Làm giảm cholesterol, chống béo phì và bảo vệ sức khỏe tim mạch; cung cấp chất sắt giảm nguy cơ thiếu máu, tốt cho mắt, phòng ngừa ung thư,...', 1, '', '', NULL, '2022-07-17 09:59:14', NULL),
(47, 2, 'Su su', '1658077276-product-su-su-2.jpg', 15000, 35, 0, '', 'Quả su su là loại thực phẩm được nhiều người ưa thích bởi hương vị ngon ngọt, có thể chế biến thành nhiều món ăn hấp dẫn cho gia đình. Ngoài ra, loại thực phẩm này cũng rất giàu dinh dưỡng. Hàm lượng vitamin cùng khoáng chất trong su su rất cao, vì thế mang đến nhiều công dụng cho sức khỏe của con người.', 1, '', '', NULL, '2022-07-17 10:01:16', NULL),
(48, 2, 'Rau xà lách', '1658077418-product-xa-lach-1.jpg', 10000, 20, 15, '', 'Trong xà lách có chứa vitamin A, folate, còn có các loại khoáng chất và vitamin như sắt, canxi, kẽm, đồng, canxi, kali, carotene, vitamin  C,... vì thế ăn xà lách giúp thanh nhiệt, làn da mịn màng, tươi sáng, ngăn ngừa ung thư, chống táo bón, giảm stress, tốt cho người thiếu máu, tốt cho mắt và người mắc bệnh tiểu đường,...', 1, '', '', '', '2022-07-17 10:03:38', '2022-07-20 15:38:01'),
(49, 2, 'Rau cải chíp', '1658077562-product-040747122face54d610dd6e94202c62e.jpg', 10000, 35, 10, '', 'Cải Chíp có giá trị dinh dưỡng rất cao, có khả năng chống oxy hóa. Đặc biệt Cải Chíp còn có công dụng: Phòng ngừa bệnh ung thư, giúp tiêu hóa tốt, hạ huyết áp cao, giảm cân, tốt cho phụ nữ mang thai và tăng cường hệ miễn dịch. ', 1, '', '', '', '2022-07-17 10:06:02', '2022-07-20 15:38:21'),
(50, 3, 'Cam Mỹ Sweet Air', '1658078136-product-cam-vang-my-moi-1.jpg', 120000, 30, 0, '', 'Cam Mỹ chứa nhiều Xenlulo có tác dụng nhuận tràng, kích thích co bóp của ruột giúp chống táo bón ( chất béo có trong đường ruột). Cam chứa nhiều Vitamin C, tốt cho da, chống lão hóa, có tác dụng phục hồi sức khỏe nhanh, tốt cho người ốm. Sử dụng cam thường xuyên sẽ tăng sức đề kháng, giảm đáng kể nguy cơ mắc bệnh sỏi thận, tránh lượng calo dư thừa. Cam có vị ngọt thơm vì vậy có thể sử dụng trực tiếp hoặc có thể ép nước, làm salad rất ngon.', 1, '', '', NULL, '2022-07-17 10:15:36', NULL),
(51, 3, 'Cherry', '1658078304-product-hinh-anh-qua-cherry.jpg', 500000, 20, 0, '', 'Cherry còn được gọi là “Siêu trái cây” với giá trị dinh dưỡng rất cao, nên đặc biệt tốt cho bà bầu, những người bị cao huyết áp. Trong quả cherry ngọt có chứa nhiêu chất dinh dưỡng như: kcal, nước, Carbs, đường, chất xơ, chất béo, protein, và Vitamin C có khả năng cung cấp đến 8% nhu cầu vitamin C hàng ngày của cơ thể. Trong quả cherry có chứa melatonin - một hormone giúp kiểm soát chu kỳ ngủ – thức, giúp ngủ ngon giấc hơn và cải thiện hiệu quả tình trạng mất ngủ.', 1, '', '', NULL, '2022-07-17 10:18:24', NULL),
(52, 3, 'Lê Hàn Quốc', '1658078402-product-le-han-quoc-02.jpg', 100000, 35, 0, '', 'Theo Đông y, lê nâu Hàn Quốc có tính mát, bình hòa không độc, có tác dụng sinh tân dịch, dưỡng huyết, thanh nhiệt, nhuận phế, tiêu đờm. Nhờ tính mát ngọt, lê rất tốt cho người bị đàm nhiệt ho khan, người bị suy nhược mất nước, người mắc đái tháo đường, người bị táo bón, người bị cao huyết áp. Với 86,5% là nước lê Hàn Quốc sẽ là nguồn thực phẩm giúp làm đẹp hiệu quả đặc biệt là với những người da khô, tàn nhang. Một trái lê có thể cung cấp 10% hàm lượng vitamin C và 1 lượng lớn canxi cho cơ thể.', 1, '', '', NULL, '2022-07-17 10:20:02', NULL),
(53, 3, 'Lựu Thái', '1658078494-product-qua-luu.jpg', 150000, 30, 0, '', 'Lựu Thái Lan quả nặng từ 400-600g vỏ màu đỏ xen lẫn vàng kem đẹp mắt. Thịt lựu có màu hồng đỏ, vị dịu ngọt thơm không chua rất ngon tương tự Lựu Tây Ban Nha. Quả lựu Thái Lan giàu chất chống oxy hóa polyphenol, giúp bảo vệ cơ thể khỏi các gốc tự do gây lão hóa, các bênh tim mạch, bệnh Azheimer và cả ung thư.', 1, '', '', NULL, '2022-07-17 10:21:34', NULL),
(54, 3, 'Mận đen Úc', '1658281925-product-1658078842-product-mận-đen-úc.jpg', 300000, 20, 0, '', 'Mận đen Black Majesty Sunny Boy là một loại trái cây chứa nhiều chất dinh dưỡng nhưng ít calories, giàu Vitamin C, chất xơ và chất chống oxy hóa. Mỗi ngày ăn một quả mận đen sẽ giúp giác ngon miệng, loại bỏ sự chán ăn do stress, ăn quá nhiều. Thường xuyên ăn mận đen sẽ giúp hệ tiêu hóa khỏe mạnh, giúp điều hòa đường ruột, cải thiện tình trạng táo bón. Nước ép trái mận có tác dụng thanh nhiệt, dùng làm thức uống giải khát cho mùa hè nóng bức.', 1, '', '', '', '2022-07-17 10:27:22', '2022-07-20 08:52:05'),
(55, 3, 'Nho xanh Nam Phi', '1658078999-product-nho-khong-hat-nam-phi-mua-o-dau-nhobonmuacom.jpg', 300000, 30, 0, '', 'Nho xanh Nam Phi là loại nho có dáng vóc nhỏ, có vị ngọt vừa phải, quả màu xanh, thịt dày cơm thơm thanh. Ăn rất giòn và không có hạt. Nho có thể ăn mọi lúc hoặc làm quà tặng. Trong nho xanh chứa nhiều Canxi, kali, photpho, sắt, vitamin B1, B2, B6, C cùng các loại axit amin cần thiết là một thần dược cho người suy nhược thần kinh, mệt mỏi quá sức. Resvartion là hợp chất chống oxy hóa cao gấp 7 lần so với cac loại vitamin E thông thường, là thực phẩm bổ dưỡng cho phụ nữ, trẻ em và những người gầy yếu thiếu máu.', 1, '', '', NULL, '2022-07-17 10:29:59', NULL),
(56, 3, 'Táo New Zealand', '1658079180-product-Capture.JPG', 200000, 35, 12, '', 'Táo Rockit New Zealand kế thừa hương thơm ngọt ngào đặc trưng, ngọt đậm, rất giòn và kích thước quả rất đều. Một quả táo có kích cỡ trung bình thường cung cấp nhiều chất xơ. táo là một trong những loại hoa quả chứa nhiều pectin, một loại chất xơ giúp giảm cholesterol. Pectin có tác dụng ngừa cholesterol hình thành trên thành mạch máu, qua đó giảm được nguy cơ bị chứng vữa xơ động mạch và bệnh tim. Nghiên cứu cho thấy, 1/3 trong số các trường hợp ung thư và 1/2 ca mắc bệnh tim mạch, huyết áp cao đều có liên quan đến chế độ ăn uống. Do táo chứa nhiều kali, một loại khoáng chất giúp kiểm soát huyết áp, nên ăn táo giúp giảm nguy cơ đột quỵ.\r\n\r\n', 1, '', '', '', '2022-07-17 10:33:00', '2022-07-20 15:09:38'),
(57, 3, 'Táo xanh Mỹ', '1658079320-product-tao-xanh-granny-smith-510x510.jpg', 100000, 50, 0, '', 'Là một trong những loại trái cây nhập khẩu được nhiều người ưa chuộng bởi vị chua đậm khác biệt với các giống táo nhập khẩu khác. Táo xanh Mỹ có nguồn gốc rõ ràng, giá trị dinh dưỡng cao như giảm lượng cholesterol xấu, chống lại các bệnh ung thư gan, ruột kết và ung thư vú. Trong táo xanh có chứa rất nhiều vitamin A, vitamin B, vitamin C,...ngoài ra còn chứa nhiều chất chống oxy tốt cho cơ thể. Trong táo còn có chứa chất khoáng cần thiết cho cơ thể như sắt, kẽm,....', 1, '', '', NULL, '2022-07-17 10:35:20', NULL),
(58, 3, 'Việt quất Highbush', '1658079574-product-sliced-and-whole-blueberries.jpg', 250000, 10, 0, '', 'Quả việt quất có kích thước nhỏ, màu xanh lục khi mới xuất hiện. Sau đó chuyển sang màu tím và xanh khi chín. Quả việt quất là một trong những loại quả giàu dinh dưỡng nhất. Một ly việt quất 148 gram có chứa: Chất xơ, Vitamin C, Vitamin KI, Một lượng nhỏ các chất dinh dưỡng khác. Chúng cũng có khoảng 85% là nước. Một cốc việt quất chỉ chứa 84 calo, với 15 gram carbohydrate. Hàm lượng calo thấp khiến cho việt quất trở thành một nguồn tuyệt vời, cung cấp một số chất dinh dưỡng quan trọng.', 1, '', '', '', '2022-07-17 10:39:34', '2022-07-18 00:42:09'),
(59, 3, 'Dâu tây Hàn Quốc', '1658079715-product-dau-tay-han-quoc-nhap-khau-01_cleanup.jpg', 200000, 20, 0, '', 'Đây là loại trái cây được nhiều người tiêu dùng tin tưởng lựa chọn bởi nó sạch và đảm bảo an toàn cho sức khỏe. Với vị ngọt, thơm ngon không chê vào đâu được, bạn có thể sử dụng dâu tây để ăn tươi, làm mứt, thêm vào bánh quế, kết hợp nấu ăn với nhiều món ăn hấp dẫn tốt cho sức khỏe. Theo nghiên cứu của các nhà khoa học, dâu tây Hàn Quốc là loại trái cây chứa đa dạng các loại vitamin và khoáng chất tốt cho sức khỏe, cung cấp lượng đường tự nhiên cao và các khoáng chất có tác dụng thuốc đẩy quá trình chuyển hóa các chất trong cơ thể, giúp lưu thông máu tốt hơn.', 1, '', '', NULL, '2022-07-17 10:41:55', NULL),
(60, 4, 'Nước ép cà chua', '1658080090-product-ImageHandler.jpg', 50000, 40, 0, '', 'Cà chua chứa hàm lượng chất dinh dưỡng cao và rất thông dụng nên rất dễ tìm mua. Đặc biệt, cà chua có ngoài tác dụng giảm cân và đẹp da, trắng da thì nước ép cà chua còn là thần dược đối với hệ tiêu hóa, giúp thúc đẩy quá trình trao đổi chất và rất tốt cho những người bị đại tràng. Ngoài ra, nước ép cà chua rất giàu vitamin C, chất này rất tốt cho tóc, xương, gan, tụy và hệ tim mạch, tác dụng làm giúp giảm cholesterol trong máu. Ngoài ra, nước ép cà chua cung cấp một lượng lycopenne phong phú giúp giảm nguy cơ mắc bệnh ung thư.', 1, '', '', NULL, '2022-07-17 10:48:10', NULL),
(61, 4, 'Nước ép cam', '1658080153-product-nuoc-cam-vfresh-hop-1l-2-org.jpg', 50000, 40, 0, '', 'Cam giàu vitamin và khoáng chất rất tốt cho sức khỏe, đặc biệt là đối với các chị em phụ nữ và những người thể trạng yếu hoặc mới ốm dậy. Uống 1 ly nước cam cung cấp tới hơn 50% nhu cầu của cơ thể đối với vitamin C cho mọi người. Nước ép cam là thần dược dành cho da, giúp tăng cường thể lực, hỗ trợ hệ tiêu hóa và là chất chống ung thư mạnh mẽ.', 1, '', '', NULL, '2022-07-17 10:49:13', NULL),
(62, 4, 'Nước ép cà rốt', '1658281850-product-1658080259-product-nuoc-ep-ca-rot.jpg', 50000, 40, 20, '', 'Cà rốt chứa hàm lượng magie, kali, canxi cao và là nguồn cung cấp đôi dào beta-carotene, axit alphalipoic, carotenoid và các loại vitamin ...vv. Vậy nước ép cà rốt có tác dụng gì? nó giúp làm tăng khả năng điều tiết của cơ thể, tăng cường thị lực và quá trình trao đổi chất, nâng cao hệ  thống miễn dịch, tăng sức đề kháng, giúp giải độc, hạ đường huyết và có tác dụng hiệu quả trong việc ngăn ngừa ung thư… Cách làm nước ép cà rốt cũng rất đơn giản, chị em có thể mua cà rốt và tự tay làm tại nhà. Loại nước ép trái trái này cũng nằm trong những loại nước ép giảm cân đẹp da ưa thích của rất nhiều chị em phụ nữ. Nước ép cà rốt còn là thức uống giúp nhuận tràng và điều trị táo bón mà bác sỹ khuyên dùng.', 1, '', '', '', '2022-07-17 10:50:59', '2022-07-20 15:10:28'),
(63, 4, 'Nước ép táo', '1658080323-product-2786da5a662704eab7b0cadf878d2ead.jpg', 50000, 40, 0, '', 'Táo cũng là 1 trong những loại nước ép giảm cân vô cùng hiệu quả của các chị em phụ nữ. Nước táo ép chứa hàm lượng cácloại vitamin như A, C, B1, B2, K cao. Đây là những chất chống oxy hóa, rất có lợi cho hệ miễn dịch và là các yếu tố cần thiết của cơ thể. Nước ép táo có công dụng điều trị bệnh thiếu máu, viêm khớp mãn tính, giúp làm giảm lượng cholesterol trong máu. Trong nước ép táo còn chứa nguyên tố Bo giúp xương chắc khỏe hơn. Để làm nước ép táo thì vô cùng đơn giản bạn chỉ cần táo và một chiếc máy xay sinh tố thôi nhé. Ngoài uống nước táo ép nguyên chất thì bạn cũng có thể kết hợp táo với các loại trái cây khác như cà rốt, dứa để tạo thành các loại nước ép táo cà rốt, nước ép táo dứa thơm ngon, bổ dưỡng.', 1, '', '', NULL, '2022-07-17 10:52:03', NULL),
(64, 4, 'Nước ép dưa hấu', '1658080401-product-15.jpg', 50000, 40, 0, '', 'Dưa hấu có nhiều công dụng thần kì đối với sức khỏe. Ngoài công dụng thanh nhiệt, giải độc, giải khát thì nước ép dưa hấu còn là liều thuốc tự nhiên rất tốt cho cơ thể. Tác dụng của nước ép dưa hấu giúp giảm cân, bổ sung năng lượng, hỗ trợ xương chắc khỏe, chống mệt mỏi và giúp phục hồi cơ bắp do nước ép dưa hấu rất giàu axit amin có tên là L-citrulline. Cách làm nước ép dưa hấu cũng rất đơn giản chỉ cần thêm một cái máy xay sinh tố. ', 1, '', '', NULL, '2022-07-17 10:53:21', NULL),
(65, 4, 'Nước ép dứa', '1658080471-product-upload_451a601d74b44f638c6d2e0e57cafce0_master.jpg', 50000, 45, 0, '', 'Nước ép dứa hay còn gọi là nước ép thơm ( người miền nam hay gọi) Ai cũng biết tác dụng của nước ép dứa trong giảm cân thì ngoài ra còn đem lại rất nhiều lợi ích mà ai cũng phải nên biết. Theo các nhà nghiên cứu với mỗi ly nước ép dứa không đường, bạn đã cung cấp cho cơ thể 130 calo, 33mg Ca, 30mg Mg và 25mmg vitamin C, tương ứng với 25%-30% lượng vitamin cần thiết mỗi ngày và đặc biệt trong nước em dứa có chứa một chất rất quan trọng là bromelain – 1 loại hợp chất thực vật. Chất bromelain chứa trong dứa  có tác dụng hỗ trợ tiêu hóa, giảm triệu chứng sổ mũi, giúp mau lành những vết thương nhỏ, đặc biệt là giúp giảm căng nhức cơ, bong gân. Bên cạnh đó, trong nước ép dứa chứa nhiều chất chống oxy hóa và có khả năng phòng chống ung thư. ', 1, '', '', '', '2022-07-17 10:54:31', '2022-07-23 09:58:52'),
(66, 4, 'Nước ép nho', '1658080528-product-47ff0441a442b31b1eb43809c07b6002.jpg', 50000, 40, 0, '', 'Quả nho có chứa hàm lượng các chất có lợi cho sức khỏe mà ít có loại quả nào sánh bằng. Nước ép nho có khả năng chống lão hóa cao, giúp giảm và làm hạn chế sự hình thành nếp nhăn nên rất được rất nhiều chị em phụ nữ lựa chọn mua. Trong nước ép nho chứa rất nhiều khoáng chất, bao gồm calcium, iodine, đồng, phosphorus, sắt và kali. Nước nho ép chữa những chứng bệnh như táo bón, bệnh tim, lao phổi, viêm khớp, bệnh gout, các vấn đề về gan, trĩ và những loại dị ứng khác nhau. Ngoài ra, Nước ép nho còn giúp phòng chống cảm lạnh, tác dụng làm trắng răng, giảm cân, bảo vệ tim, giảm lượng đường trong máu, chống viêm và giúp phục hồi cơ bắp....vv. Uống nước ép nho mỗi ngày giúp bảo vệ tim và phòng tránh các căn bệnh ung thư.', 1, '', '', NULL, '2022-07-17 10:55:28', NULL),
(67, 4, 'Nước ép ổi', '1658080581-product-1010_i5cac038341263.jpg', 50000, 40, 0, '', 'Quả ổi chứa hàm lượng dinh dưỡng rất cao, ít chất béo bão hoà, cholesterol và natri nhưng chứa nhiều chất xơ, vitamin C, A, kẽm, mangan và kali… Uống nước ép ổi thường xuyên và uống đúng cách có tác dụng tăng cường hệ thống miễn dịch, giúp làm đẹp da do nước ép ổi chứa lượng vitamin C tuyệt vời có tác dụng giúp chống lão hóa và hỗ trợ quá trình giảm cân hiệu quả và nhanh chóng. Ngoài ra, loại nước ép dinh dưỡng này còn giúp tăng cường hệ miễn dịch, giảm đường huyết, ngăn ngừa tiêu chảy, táo bón và bảo vệ cơ thể chống ung thư.', 1, '', '', NULL, '2022-07-17 10:56:21', NULL),
(68, 5, 'Mít sấy giòn', '1658080996-product-timthumb.jpg', 150000, 30, 0, '', 'Mít là thức ăn bổ dưỡng giúp ngăn ngừa bệnh tim mạch, tốt cho mắt, ngăn ngừa ung thư, hỗ trợ giảm cân, chống lão hóa da, hỗ trợ hệ tiêu hóa và tốt cho xương khớp. Vì chứa hàm lượng vitamin C cao nên khoai lang tím giúp làm đẹp da, ngăn ngừa dấu hiệu lão hóa như nếp nhăn, đốm nâu, tránh tình trạng bị khô da, da thiếu nước, là thực phẩm lý tưởng hỗ trợ việc giảm cân, thừa cân, béo phì.', 1, '', '', NULL, '2022-07-17 11:03:16', NULL),
(69, 5, 'Đậu Hà Lan sấy giòn', '1658281741-product-1658081065-product-timthumb-6.jpg', 100000, 50, 0, '', 'Đậu Hà Lan có chứa hệ thống dưỡng chất đầy ấn tượng. Chúng cung cấp lượng calo tương đối thấp với khoảng 70% trong số đó tới từ carbs, phần còn lại tới từ đạm và lượng nhỏ chất béo. Hơn nữa, đậu Hà Lan chứa gần như toàn bộ các loại khoáng chất mà cơ thể chúng ta cần cùng với lượng chất xơ đáng kể. Là một nguồn dinh dưỡng tuyệt vời. Một chén đậu Hà Lan sấy chứa 16 gam protein và 16 gam chất xơ. Chúng cũng là một nguồn cung cấp kali tuyệt vời, chứa 20% giá trị hàng ngày chỉ trong một chén đậu Hà Lan sấy.', 1, '', '', '', '2022-07-17 11:04:25', '2022-07-20 08:49:01'),
(70, 5, 'Chuối sấy giòn', '1658281659-product-1658081140-product-timthumb-2.jpg', 100000, 30, 0, '', 'Thực phẩm này là rất thấp trong chất béo bão hòa, cholesterol và natri. Nó cũng là một nguồn tốt của xơ, Vitamin C, kali và mangan, và một nguồn rất tốt của Vitamin B6. Liều lượng kali cao có trong chuối cũng đồng nghĩa bạn sẽ có một trái tim và hệ thống thần kinh khỏe mạnh. Kali hỗ trợ sự co cơ và do đó, mang lại lợi ích cho nhịp đập của tim, tiêu hóa và những chuyển động cơ bắp khác, đó là lý do tại sao nó là thức ăn nhẹ tuyệt vời trước và sau khi tập luyện.', 1, '', '', '', '2022-07-17 11:05:40', '2022-07-20 08:47:39'),
(71, 5, 'Dừa sấy giòn', '1658243908-product-1658081200-product-timthumb-3.jpg', 125000, 30, 0, '', 'Dừa sấy giòn là một sản phẩm mới trên thị trường được phát triển trên sản phẩm mứt dừa truyền thống. Điểm khác biệt của 2 dòng sản phẩm dừa sấy giòn và mứt dừa truyền thống là việc mứt dừa sấy giòn được sản xuất dựa trên công nghệ hiện đại, Sản phẩm được chế biến từ 100% cơm dừa tươi xuất xứ Bến Tre theo phương pháp sấy nóng liên tục tuần hoàn.làm cho sản phẩm có độ giòn, tan và thành phần đường có trong sản phẩm cũng được sử dụng ít hơn. ', 1, '', '', '', '2022-07-17 11:06:40', '2022-07-19 22:18:28'),
(72, 5, 'Hạt điều ', '1658243868-product-1658081321-product-timthumb-4.jpg', 225000, 50, 0, '', 'Được lựa chọn kỹ lưỡng từ những hạt điều tươi qua quy trình khép kín sấy và đóng gói, không những đảm bảo nguyên vẹn hương vị đặc trưng và thành phần dinh dưỡng của hạt điều, mà còn tạo ra những hạt đều, chắc, to và đẹp mắt với màu vàng nhạt thể hiện độ mới của sản phẩm. Đặc biệt, hàm lượng đường rất ít trong sản phẩm, điều đó rất có lợi cho sức khỏe người tiêu dùng.', 1, '', '', '', '2022-07-17 11:08:41', '2022-07-19 22:17:48'),
(73, 5, 'Hồng sấy khô Hàn Quốc', '1658243787-product-1658081445-product-timthumb-5.jpg', 800000, 10, 0, '', 'Những quả Hồng khô trái đều rất tinh tế và đẹp mắt. Loại hồng được sử dụng để làm sấy khô là những quả hồng to tròn, rất ngọt và không có hạt. Hồng khô được sấy khô tự nhiên, không hề chứa chất bảo quản hay tẩm ướp thêm bất cứ chất phụ gia nào nên rất an toàn cho sức khỏe. Hồng khô của người Hàn còn có một số tác dụng chữa bệnh nhất định.có tác dụng rất tốt trong việc hỗ trợ điều trị bệnh cao huyết áp, các bệnh đến đường huyết..', 1, '', '', '', '2022-07-17 11:10:45', '2022-07-19 22:16:27'),
(74, 5, 'Hoa quả sấy thập cẩm', '1658243738-product-1658081814-product-timthumb-1.jpg', 50000, 50, 20, '', 'Được sản xuất theo công nghệ sấy chân không, một công nghệ mới tiên tiến nhất nên sản phẩm sau khi sấy vẫn giữ được nguyên hương vị của nó, màu sắc của thiên nhiên, các Vitamin của sản phẩm không bị biến đổi như khi sấy ở nhiệt độ cao, chất béo thấp, tính khô giòn tạo cảm giác ngon miệng.\r\nLà một sản phẩm chứa đựng toàn bộ các sản phẩm khác: chuối, khoai môn, khoai lang vàng, khoai lang tím, mít, khổ qua, cà rốt….. Thập cẩm sấy chứa ít chất béo bão hòa,cholesterol, giàu axit béo omega 3, omega 6. Ngoài ra còn có vitamin tốt cho cơ vitamin A, vitamin C, vitamin E và vitamin B6,và khoáng chất như sắt, magie, phốt pho, kali và mangan, và hàm lượng chất xơ rất cao.', 1, '', '', '', '2022-07-17 11:16:54', '2022-07-20 15:10:10'),
(75, 7, 'Thùng sữa TH true milk socola', '1658082082-product-thung-sua-tuoi-tiet-trung-th-true-milk-so-co-la-hop-110ml-48-hop-201812031638537275.jpg', 280000, 40, 0, '', 'Đảm bảo không sử dụng thêm hương liệu, vị ngon 100% đến từ sữa tươi từ trang trại của TH True Milk chứa nhiều vitamin và khoáng chất như Vitamin A, D, B1, B2, canxi, kẽm. Thùng 48 hộp sữa tươi tiệt trùng socola TH true MILK 110ml đóng thùng tiện lợi, tiết kiệm, socola thơm ngon', 1, '', '', NULL, '2022-07-17 11:21:23', NULL),
(76, 7, 'Thùng sữa tươi TH true milk hương dâu', '1658082159-product-thung-48-hop-sua-tuoi-tiet-trung-huong-dau-th-true-milk-110ml-202104081700130078.jpg', 280000, 30, 0, '', 'Đảm bảo không sử dụng thêm hương liệu, vị ngon 100% đến từ sữa tươi từ trang trại của TH True Milk nên được các bà mẹ ưu tiên lựa chọn hàng đầu. Thùng 48 hộp sữa tươi tiệt trùng hương dâu TH true MILK 110ml đóng thùng tiện lợi, tiết kiệm, có thể dùng lâu dài', 1, '', '', NULL, '2022-07-17 11:22:39', NULL),
(77, 7, 'Thùng sữa lúa mạch milo', '1658082273-product-thung-48-hop-loc-4-thuc-uong-lua-mach-milo-active-go-115ml-202201131354173373.jpg', 280000, 50, 0, '', 'Sản phẩm sữa uống lúa mạch thơm ngon, giàu canxi và protein cho cơ thể phát triển. Vì vậy, sữa lúa mạch Milo rất được các bé yêu thích và ưa chuộng. Thùng 48 hộp thức uống lúa mạch Milo Active Go 115ml đóng thùng  tiện lợi, phù hợp sử dụng lâu dài và cung cấp dinh dưỡng cho sinh hoạt hằng ngày.', 1, '', '', '', '2022-07-17 11:24:33', '2022-07-22 09:11:11'),
(78, 7, 'Thùng sữa tươi vinamilk', '1658082337-product-thung-48-hop-sua-tuoi-co-duong-vinamilk-100-sua-tuoi-180ml-202006101848226080.jpg', 280000, 50, 0, '', 'Được chế biến từ nguồn sữa tươi 100% chứa nhiều dưỡng chất như vitamin A, D3, canxi,... tốt cho xương và hệ miễn dịch. Sữa tươi Vinamilk là thương hiệu được tin dùng hàng đầu với chất lượng tuyệt vời. Thùng 48 hộp sữa tươi có đường Vinamilk 100% Sữa Tươi 180ml thơm ngon dễ uống', 1, '', '', NULL, '2022-07-17 11:25:37', NULL),
(79, 7, 'Thùng sữa tươi vinamlik hương dâu', '1658082401-product-thung-48-hop-sua-tuoi-huong-dau-vinamilk-110ml-202207152138429066.jpg', 280000, 30, 5, '', 'Được chế biến từ nguồn sữa tươi 100% chứa nhiều dưỡng chất như vitamin A, D3, canxi,... tốt cho xương và hệ miễn dịch. Sữa tươi Vinamilk là thương hiệu được tin dùng hàng đầu với chất lượng tuyệt vời. Thùng 48 hộp sữa tươi hương dâu Vinamilk 100% Sữa Tươi 110ml hương dâu dễ uống', 1, '', '', '', '2022-07-17 11:26:41', '2022-07-20 15:11:24'),
(80, 7, 'Thùng sữa yomost hương cam', '1658082483-product-thung-48-hop-sua-chua-uong-huong-cam-yomost-170ml-202104091434102010.jpg', 300000, 30, 0, '', 'Thương hiệu sữa chua Yomost mang đến những dòng sản phẩm được làm từ sữa chua lên men tự nhiên, kết hợp với trái cây thơm ngon, cung cấp nhiều loại dinh dưỡng cần thiết và protein cho cơ thể. Thùng 48 hộp sữa chua uống hương cam YoMost 170ml đóng thùng tiện lợi và có hương cam thơm lừng.', 1, '', '', NULL, '2022-07-17 11:28:03', NULL),
(81, 7, 'Thùng sữa kun vị kem dâu', '1658082552-product-scu-kun-vi-kem-dau-hop-180ml-thung-48-hop-2-org.jpg', 300000, 30, 0, '', 'Sữa chua uống cũng cung cấp rất nhiều dinh dưỡng cho bé yêu. Sữa chua uống LiF Kun với hương vị thơm ngon từ trái cây, bổ sung cho bé vitamin A, D3, K2 và canxi, cho bé luôn phát triển toàn diện. Thùng 48 hộp sữa chua uống hương kem dâu LiF Kun 180ml hương dâu thơm lừng, ngon ngọt đến giọt cuối cùng', 1, '', '', NULL, '2022-07-17 11:29:12', NULL),
(82, 7, 'lốc sữa TH true milk hương việt quất', '1658082654-product-loc-4-hop-sua-chua-uong-huong-viet-quat-th-true-yogurt-180ml-202104182345102539.jpg', 30000, 60, 0, '', 'Sữa chua uống TH True Yogurt với vị ngon hoàn toàn từ sữa tươi, đảm bảo không có sữa bột, kết hợp với hương vị trái cây tự nhiên mang đến vị ngon tuyệt vời. Lốc 4 hộp sữa chua uống hương việt quất TH True Yogurt 180ml hương việt quất thơm ngon, kích thích tiêu hoá', 1, '', '', NULL, '2022-07-17 11:30:54', NULL),
(83, 7, 'Lốc sữa tươi vinamilk socola', '1658082783-product-loc-4-hop-sua-tuoi-socola-vinamilk-100-sua-tuoi-180ml-202104091037020668.jpg', 30000, 60, 0, '', 'Được chế biến từ nguồn sữa tươi 100% chứa nhiều dưỡng chất như vitamin A, D3, canxi,... tốt cho xương và hệ miễn dịch. Sữa tươi Vinamilk là thương hiệu được tin dùng hàng đầu với chất lượng tuyệt vời. Lốc 4 hộp sữa tươi socola Vinamilk 100% Sữa Tươi 180ml hương socola dễ uống', 1, '', '', NULL, '2022-07-17 11:33:03', NULL),
(84, 7, 'Lốc sữa tươi vinamilk hương dâu', '1658082834-product-loc-4-hop-sua-tuoi-huong-dau-vinamilk-100-sua-tuoi-180ml-202104190010241131.jpg', 32000, 0, 0, '', 'Được chế biến từ nguồn sữa tươi 100% chứa nhiều dưỡng chất như vitamin A, D3, canxi,... tốt cho xương và hệ miễn dịch. Sữa tươi Vinamilk là thương hiệu được tin dùng hàng đầu với chất lượng tuyệt vời. Lốc 4 hộp sữa tươi hương dâu Vinamilk 100% Sữa Tươi 180ml hương dâu dễ uống', 1, '', '', '', '2022-07-17 11:33:54', '2022-07-20 09:45:59'),
(85, 7, 'Lốc sữa tươi TH true milk', '1658082900-product-loc-4-hop-sua-tuoi-tiet-trung-vi-tu-nhien-th-true-milk-hilo-180ml-202205200918447879_300x300.jpg', 30000, 60, 0, '', 'Sữa tươi thơm ngon, bổ dưỡng của thương hiệu sữa tươi TH true Milk là sự lựa chọn an toàn cho bạn. Lốc 4 hộp sữa tươi tiệt trùng vị tự nhiên TH true MILK Hilo 180ml giúp hỗ trợ xương chắc khỏe, cung cấp các dưỡng chất cần thiết cho cơ thể. Thành phần an toàn, đã được kiểm nghiệm nghiêm ngặt.', 1, '', '', NULL, '2022-07-17 11:35:00', NULL);

